mapper.update:
update emp
set NAME = #{name,jdbcType=VARCHAR},
  PRICE = #{price,jdbcType=DOUBLE},
  createTime = #{createtime,jdbcType=TIMESTAMP},
  sex = #{sex,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select ID, NAME, PRICE, createTime, sex
from emp
where ID = #{id,jdbcType=VARCHAR}
