mapper.update:
update user
set NICKNAME = #{nickname,jdbcType=VARCHAR},
  NAME = #{name,jdbcType=VARCHAR},
  PHONE = #{phone,jdbcType=VARCHAR},
  SEX = #{sex,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select ID, NICKNAME, NAME, PHONE, SEX
from user
where ID = #{id,jdbcType=VARCHAR}
